export const initialState ={
    date: undefined,
    city: undefined,
    option:{
        children: undefined,
        rooms: undefined,
        adults: undefined,
    }
}

export const reducer = (state, action) =>{
    switch(action.type){
        case "NEW_SEARCH":
            return action.payload
        case "RESET_SEARCH":
            return initialState
        default:
            return state
    }
}
