import React, { useState } from 'react'
import './header.css'
import Option from './Option'
import {DateRange} from 'react-date-range'
import 'react-date-range/dist/styles.css'; // main style file
import 'react-date-range/dist/theme/default.css'; // theme css file

function Header() {
    const [openDate, setopenDate] = useState(false)
    const [openOption, setopenOption] = useState(false)
    const [option, setoption] = useState({
        adults: 0,
        children: 0,
        rooms: 0
    })
    const [date, setDate] = useState([
        {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection'
        }
    ]);
    const handleclick = () =>{
        if(openDate){
            setopenDate(false)
        }
        else{
            setopenDate(true)
            setopenOption(false)
        }
    }
    const handleoption = () =>{
        if(openOption){
            setopenOption(false)
        }
        else{
            setopenOption(true)
            setopenDate(false)
        }
    }
    const increment = () => {
        setoption({})
    }
    // const handleSelect(ranges) => {
    //     console.log(ranges);
    //     // {
    //     //   selection: {
    //     //     startDate: [native Date Object],
    //     //     endDate: [native Date Object],
    //     //   }
    //     // }
    // }
    return (
        <div className="header">
            <ul className='Options'>
                <li><Option title={"Stays"} /></li>
                <li><Option title={"Flights"} /></li>
                <li><Option title={"Car Rentals"} /></li>
                <li><Option title={"Airport Taxi"} /></li>
            </ul>
            <p className="desc">Lorem ipsum dolor sit amet consectetur adipisicing elit. Assumenda, deleniti.?</p>
            <p className="desc2">Lorem ipsum dolor sit, amet consectetur adipisicing elit. Provident odio alias quod odit molestiae velit eos, iusto explicabo rerum perspiciatis.</p>
            <button className="btn btn-header btn-option">Sign In/Register</button>
            <div className="search-header">
                <div className="search-item">
                    <input type="text" placeholder='Where to ?' className=''/>
                </div>
                <div className="search-item" onClick={() => {handleclick()}}>
                    <span>Select Date</span>
                </div>
                {openDate && 
                    (<DateRange
                    editableDateInputs={true}
                    onChange={(item) => setDate([item.selection])}
                    moveRangeOnFirstSelection={false}
                    ranges={date}
                    className="date"
                    minDate={new Date()}
                    />)
                }
                <div className="search-item" onClick={() => {handleoption()}}>
                    <span>{option.adults} Adults . {option.children} Children . {option.rooms} Rooms</span>
                    {openOption &&
                        (<div className="option1">
                            <div className="option-item">
                                <span className="item">Adults</span>
                                <div className="Counter">
                                    <button className="optionCounter" onClick={() =>{decrement()}}>-</button>
                                    <span className="counterNum">{option.adults}</span>
                                    <button className="optionCounter" onClick={() =>{increment()}}>+</button>
                                </div>
                            </div>
                            <div className="option-item">
                                <span className="item">Children</span>
                                <div className="Counter">
                                    <button className="optionCounter" onClick={() =>{decrement()}}>-</button>
                                    <span className="counterNum">{option.children}</span>
                                    <button className="optionCounter" onClick={() =>{increment()}}>+</button>
                                </div>
                            </div>
                            <div className="option-item">
                                <span className="item">Rooms</span>
                                <div className="Counter">
                                    <button className="optionCounter" onClick={() =>{decrement()}}>-</button>
                                    <span className="counterNum">{option.rooms}</span>
                                    <button className="optionCounter" onClick={() =>{increment()}}>+</button>
                                </div>
                            </div>
                        </div>)
                    }
                </div>
                <div className="search-item">
                    <button className="btn search-btn bg-success">Search</button>
                </div>
            </div>
        </div>
    )
}

export default Header
