export const initialState ={
    dates: undefined,
    city: undefined,
    option:{
        children: undefined,
        rooms: undefined,
        adults: undefined,
    },
    data: undefined,
}

export const reducer = (state, action) =>{
    switch(action.type){
        case "NEW_SEARCH":
            return action.payload
        case "RESET_SEARCH":
            return initialState
        case "SET_DATA":
            return 
        default:
            return state
    }
}
