import React, { useState } from 'react'
import { useAuthValue } from './AuthLayer'
import "./login.css"
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

function Login() {
    const navigate = useNavigate()
    const [credentials, setCredentials] = useState({
        name: undefined,
        password: undefined
    })
    const [forgot, setForgot] = useState(false)
    const [state, dispatch] = useAuthValue()
    const handleChange = (e) =>{
        setCredentials({
            ...credentials,
            [e.target.id]: e.target.value
        })
        // console.log(credentials)
    }
    const handlePass = (e) => {
        setForgot(true)
    }
    const handleClick = async (e) =>{
        e.preventDefault()
        try {
            // console.log(credentials)
            const res = await axios.post("http://localhost:8800/api/auth/login", credentials)
            console.log(res.data.details)
            // console.log("SUCCESS")
            localStorage.setItem('user', JSON.stringify(res.data.details))
            dispatch({type: "LOGIN_SUCCESS"})
            navigate("/")
        } catch (err) {
            dispatch({type: "LOGIN_FAILURE", error: err.response?.data})
            console.log(err)
        }
    }
    console.log(state.error)
    return (
        <div className="login">
            {forgot ?
                <div className="lContainer">
                    <input type="text" placeholder = "username" id = "name" onChange = {handleChange} className="lInput" />
                    <input type="password" placeholder = "password" id = "password" onChange = {handleChange} className="lInput" />
                    <button className="lbutton" onClick={handleClick}>Login</button>
                    <button className="lbutton" onClick={handlePass}>Forgot Password</button>
                </div>
                :
                <div className="lContainer">
                    <input type="text" placeholder = "username" id = "name" onChange = {handleChange} className="lInput" />
                    <input type="password" placeholder = "old_password" id = "old_password" onChange = {handleChange} className="lInput" />
                    <button className="lbutton" onClick={handleClick}>Submit</button>
                </div>
            }
        </div>
    )
}

export default Login
