import React, { useEffect, useState } from 'react'
import './review.css'
import Qna from './Qna'
import { useFetch } from './useFetch'
import axios from 'axios'
import { useAuthValue } from './AuthLayer'

function Review({id, comments}) {
  const [data, setData] = useState([])
  const [list, setList] = useState(comments)
  const [question, setQuestion] = useState("")
  const [state, dispatch] = useAuthValue()
  const url1 =  `http://localhost:8800/api/hotels/find/${id}`
  // const url2 =  `http://localhost:8800/api/comments/comment/:id`
  const getList = async () => {
    let Data = []
    try {
      // const List = await axios.get(url1)
      // console.log("ebfehbvcf",List.data.comments[0])
      for(const item of list){
        const res = await axios.get(`http://localhost:8800/api/comments/comment/${item}`)
        Data.push(res.data) 
      }
      setData(Data)
    } catch (error) {
      
    }
  }
  const handleAsk = async() => {
    try {
      const res = await axios.post(`http://localhost:8800/api/comments/hotelCom/${id}`, {user_id: state.user.name, comment: question, answer : []})
      setList(res.data.comments)
    } catch (error) {
      alert("Error")
    }
  }
  // const getData = async () => {
  //   let Data = []
  //   try {
  //     await Promise.all(
  //       list.map((item) =>{
  //         const res = axios.get(`http://localhost:8800/api/comments/comment/${list[0]}`)
  //         Data.push(res.data)
  //       })
  //     )
  //     setData(Data)
  //   } catch (error) {
      
  //   }
  // }
  console.log("rwfref", list)
  useEffect(() =>{
    getList()
    // getData()
  },[list])
  useEffect(() =>{
    setList(comments)
  },[comments])
  // console.log(list)
  console.log(data)
  return (
    <div className="review">
        <div className="review_container">
            <div className="review_header">
                <h1>QnA</h1>
            </div>
            <div className="review_Questions">
              <input type="text" placeholder='Ask a question' onChange={(e) => {setQuestion(e.target.value)}}/>
              <button onClick={() => handleAsk()}>Ask!</button>
            </div>
            <hr />
            <div className="review_qna_box">
              {
                list?.map((item) =>(
                  <>
                  {/* <Qna Data = {item}/> */}
                  <hr />
                  </>
                ))
              }
            </div>
        </div>
    </div>
  )
}

export default Review
