import React, { useEffect, useState } from 'react'
import './review.css'
import Qna from './Qna'
import { useFetch } from './useFetch'
import axios from 'axios'

function Review({id}) {
  const [data, setData] = useState([])
  const [question, setQuestion] = useState("")
  const url1 =  `http://localhost:8800/api/hotels/find/${id}`
  // const url2 =  `http://localhost:8800/api/comments/comment/:id`
  const getList = async () => {
    let Data = []
    try {
      const List = await axios.get(url1)
      // console.log("ebfehbvcf",List.data.comments[0])
      for(const item of List.data.comments){
        const res = await axios.get(`http://localhost:8800/api/comments/comment/${item}`)
        Data.push(res.data) 
      }
      setData(Data)
    } catch (error) {
      
    }
  }
  // const getData = async () => {
  //   let Data = []
  //   try {
  //     await Promise.all(
  //       list.map((item) =>{
  //         const res = axios.get(`http://localhost:8800/api/comments/comment/${list[0]}`)
  //         Data.push(res.data)
  //       })
  //     )
  //     setData(Data)
  //   } catch (error) {
      
  //   }
  // }
  useEffect(() =>{
    getList()
    // getData()
  },[])
  // console.log(list)
  console.log(data)
  return (
    <div className="review">
        <div className="review_container">
            <div className="review_header">
                <h1>QnA</h1>
            </div>
            <div className="review_Questions">
              <input type="text" placeholder='Ask a question' onChange={(e) => {setQuestion(e.target.value)}}/>
              <button>Ask!</button>
            </div>
            <hr />
            <div className="review_qna_box">
              {
                data.map((item) =>(
                  <>
                  <Qna Data = {item}/>
                  <hr />
                  </>
                ))
              }
            </div>
        </div>
    </div>
  )
}

export default Review
